    private void bntRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bntRegistrarActionPerformed
        registrarVenta();
    }//GEN-LAST:event_bntRegistrarActionPerformed
    private void registrarVenta() {
        // Obtener el cÃ³digo del plato a vender
        int codigoPlato = Integer.parseInt(txt_Codigo.getText());

        // Verificar que el cÃ³digo del plato sea vÃ¡lido
        cls_Plato plato = null;
        for (int i = 0; i < platos.length; i++) {
            if (platos[i] != null && platos[i].getCodigoInt() == codigoPlato) {
                plato = platos[i];
                break;
            }
        }
        if (plato == null) {
            JOptionPane.showMessageDialog(this, "No se encontrÃ³ el plato con el cÃ³digo " + codigoPlato);
            return;
        }

        // Obtener la cantidad a vender
        int cantidad = Integer.parseInt(txt_cantidad.getText());

        // Verificar que la cantidad sea vÃ¡lida
        if (cantidad <= 0) {
            JOptionPane.showMessageDialog(this, "La cantidad debe ser mayor a cero");
            return;
        }

        // Registrar la venta
        float subtotal = plato.getPrecioFlt() * cantidad;

        // AquÃ­ puedes agregar lÃ³gica para actualizar el inventario
        JOptionPane.showMessageDialog(this, "Se registrÃ³ la venta del plato " + plato.getNombreStr() + " en cantidad de " + cantidad + " por un total de $" + subtotal);

        // Limpiar los campos de texto
        
        txt_cantidad.setText("");

        // Actualizar los campos de texto con el total de ganancias y el nÃºmero de ventas
        totalGanado += subtotal;

        // Mostrar el total de ganancias
         mostrarTotalGanado();
             numeroDeVentas++;

        // Mostrar el nÃºmero de ventas
        mostrarNumeroDeVentas();
        
    }    
    private void bntCancelarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bntCancelarActionPerformed
        txt_cantidad.setText("");
    }//GEN-LAST:event_bntCancelarActionPerformed
    private void mostrarTotalGanado() {
    txt_totalGanado.setText(String.valueOf(totalGanado));
}
    private void mostrarNumeroDeVentas() {
    txt_numeroDeVentas.setText(String.valueOf(numeroDeVentas));
}





